class MarvelService {
    getResource = async (url) => {
        let res = await fetch(url);

        let myKey = '60666e984e5019fa8fdfd6ec40b7e3f4&'

        if (!res.ok) {
            throw new Error(`Could not fetch ${url}, status: ${res.status}`);
        }

        return await res.json();
    }
    getAllCharacters = () => {
        return this.getResource(`http://gateway.marvel.com/v1/public/comics?ts=1&apikey=${myKey}`);
    }
}

export default MarvelService;
