import './charList.scss';
import PropTypes from 'prop-types';
import React from 'react';
import MarvelService from '../../services/MarvelService'
import ErrorMessage from '../errorMessage/ErrorMessage'
import Spinner from '../spinner/spinner';

class CharList extends React.Component {

    state = {
        charList: [],
        loading: true,
        error: false,
        newItemLoading: false,
        offset: 210,
        charEnded: false
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.onRequest()
    }

    onRequest = (offset) => {
        this.onCharListLoading()
        this.marvelService.getAllCharacters(offset)
            .then(this.onCharListLoaded)
            .catch(this.onError)
    }

    onCharListLoading = () => {
        this.setState({
            newItemLoading: true
        })
    }

    onCharListLoaded = (newCharList) => {

        let ended = false
        if (newCharList.length < 9) {
            ended = true
        }

        this.setState(({ offset, charList }) => ({
            charList: [...charList, ...newCharList],
            loading: false,
            newItemLoading: false,
            offset: offset + 9,
            charEnded: ended
        }))
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true
        })
    }

    temRefs = [];

    setRef = (ref) => {
        this.itemRefs.push(ref);
    }

    focusChar = (id) => {
        this.itemRefs.forEach(item => item.classList.remove('char__item_selected'));
        this.itemRefs[id].classList.add('char__item_selected');
        this.itemRefs[id].focus();
    }

    // Этот метод создан для оптимизации, 
    // чтобы не помещать такую конструкцию в метод render
    renderItems(arr) {
        const { focusActive } = this.state
        const items = arr.map((elt, i) => {
            let imgStyle = { 'objectFit': 'cover' }
            if (elt.thumbnail === 'http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available.jpg') {
                imgStyle = { 'objectFit': 'unset' }
            }
            return (
                <li
                    key={elt.id}
                    tabIndex={0}
                    ref={this.setRef}
                    className='char__item'
                    onClick={() => {
                        this.props.onCharSelected(elt.id)
                        this.focusChar(i)
                    }}>
                    <img src={elt.thumbnail} alt="abyss" style={imgStyle} />
                    <div className="char__name">{elt.name}</div>
                </li>
            )
        })
        // А эта конструкция вынесена для центровки спиннера/ошибки
        return (
            <ul className="char__grid">
                {items}
            </ul>
        )
    }

    render() {

        const { charList, loading, error, newItemLoading, charEnded, offset } = this.state
        const items = this.renderItems(charList)

        const content = !(loading || error) ? items : null
        const errorMessage = error ? <ErrorMessage /> : null
        const spinner = loading ? <Spinner /> : null

        return (
            <div className="char__list" >
                {content}
                {errorMessage}
                {spinner}
                <button
                    disabled={newItemLoading}
                    style={{ 'display': charEnded ? 'none' : 'block' }}
                    onClick={() => this.onRequest(offset)}>
                    <div className="inner">load more</div>
                </button>
            </div >
        )
    }
}

CharList.propTypes = {
    onCharSelected: PropTypes.func.isRequired
}

export default CharList;
