import './charList.scss';
import abyss from '../../resources/img/abyss.jpg';
import React from 'react';
import MarvelService from '../../services/MarvelService'

class CharList extends React.Component {

    state = {
        charList: [],
        loading: true,
        error: false
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.marvelService
            .getAllCharacters()
            .then(this.onCharListLoaded)
            .catch(this.onError)
    }

    onCharListLoaded = (charList) => {
        this.setState({
            charList,
            loading: false
        })
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true
        })

    }

    // Этот метод создан для оптимизации, 
    // чтобы не помещать такую конструкцию в метод render
    renderItems(arr) {
        const items = arr.map(elt => {
            let imgStyle = { 'objectFit': 'cover' }
            if (elt.thumbnail === 'http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available.jpg') {
                imgStyle = { 'objectFit': 'cover' }
            }
            return (
                <li key={elt.id} className="char__item">
                    <img src={elt.thumbnail} alt="abyss" />
                    <div className="char__name">{elt.name}</div>
                </li>
            )
        })
        // А эта конструкция вынесена для центровки спиннера/ошибки
        return (
            <ul className="char__grid">
                {items}
            </ul>
        )
    }

    render() {

        const { charList, loading, error } = this.state
        console.log(this.renderItems(this.state.charList))
        return (
            <div className="char__list" >
                <ul className="char__grid">
                    <li className="char__item">
                        <img src={abyss} alt="abyss" />
                        <div className="char__name">Abyss</div>
                    </li>
                </ul>
                <button className="button button__main button__long">
                    <div className="inner">load more</div>
                </button>
            </div>
        )
    }
}

export default CharList;
